package practise_programs;

public class OperatorExample11 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;  
		int b=5;  
		int min=(a<b)?a:b;  
		System.out.println(min);  
		int c=10;  
		int d=5;  
		int mini=(c<d)?c:d;  
		System.out.println(mini);  

	}

}
