package practise_programs;

import java.util.Scanner;

public class Unary1 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
        
        int x=100;
        System.out.println(x++);
        System.out.println(++x);
        System.out.println(x--);
        System.out.println(--x);



	}

}
