package practise_programs;

public class Unary4 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int a=40;
		int b=60;
		System.out.println(a++ + ++a);
		System.out.println(++a + ++a);
		System.out.println(++a + a++);
		System.out.println(a++ + a++);
	
		System.out.println(a++ + b++);
		System.out.println(++a + b++);
		System.out.println(a++ + ++b);
		System.out.println(++a + ++b);
		
		System.out.println(b++ + ++b);
		System.out.println(++b + ++b);
		System.out.println(++b + ++b);
		System.out.println(b++ + b++);
	
		
	}

}
